let counter = context.get("counter") || 1;
let pressure_ = context.get("pressure") || 10;
let temperature_ = context.get("temperature") || 23;

let max_batt = 3.2;
let min_batt = 3.1;
let pressure = Number(((Math.random() * 10) + pressure_).toFixed(2));
let temperature = Number(((Math.random() * 1.2) + temperature_).toFixed(2));

msg.payload.sensor_data.pressure = pressure;
msg.payload.sensor_data.temperature = temperature;

msg.payload.battery = ((Math.random() * (max_batt - min_batt) + min_batt).toFixed(2)).toString();
msg.payload.battery_percent = ((msg.payload.battery * 100 / 3.3).toFixed(2)).toString();
msg.payload.counter = counter++;

context.set("pressure", pressure_);
context.set("temperature", temperature_);
context.set("counter", counter);
return msg;
