let count = context.get("counter") || 1;
/* 
Ranges:
db: 0-100
raw adc: 0-32000
mA: 0-20
*/
let max_raw = 32000;
let min_raw = 30000;
let max_batt = 3.2;
let min_batt = 3.1;

let raw = Number((Math.random() * (max_raw - min_raw) + min_raw).toFixed(2));
let ma = Number((raw / 1600).toFixed(2));
let db = Number((raw / 320).toFixed(0));

msg.payload.raw_adc = raw;
msg.payload.ma = ma;
msg.payload.db = db;
msg.data.sensor_data.raw_adc = raw;
msg.data.sensor_data.ma = ma;
msg.data.sensor_data.db = db;
msg.data.battery = ((Math.random() * (max_batt - min_batt) + min_batt).toFixed(2)).toString();
msg.data.battery_percent = ((msg.data.battery * 100 / 3.3).toFixed(2)).toString();
msg.data.counter = count++;

context.set("counter", count);
return msg;
